import {Component, OnInit, AfterViewInit, Inject} from '@angular/core';
import {MAT_DIALOG_DATA, MatDialogRef} from '@angular/material/dialog';
import {Essay} from '../../models/essays/essay';
import {Sleep} from '../../utilities/sleep';
import {EssayType} from '../../models/essays/essay-type';
import {EssayService} from '../../services/essay.service';
import {FormControl} from '@angular/forms';
import {Observable} from 'rxjs';
import {map, startWith} from 'rxjs/operators';

export interface DialogData {
  showExisting: boolean;
  showNew: boolean;
}

class QuickEssayReturnData {
  essay: Essay;
  isExisting: boolean;
}

@Component({
  selector: 'app-quick-essay',
  templateUrl: './quick-essay.component.html',
  styleUrls: ['./quick-essay.component.scss']
})
export class QuickEssayComponent implements OnInit, AfterViewInit {
  public essay: Essay;

  public essayTypes: EssayType[];

  public searchEssays: Essay[] = [];

  public essayTitleAutocompleteControl = new FormControl();
  public essayTitleFilteredOptions: Observable<Essay[]>;
  public essayTitleFieldDisplayValue: string;

  private userId: string;

  private returnData: QuickEssayReturnData;

  constructor(public dialogRef: MatDialogRef<QuickEssayComponent>,
              private essayService: EssayService,
              @Inject(MAT_DIALOG_DATA) public data: DialogData) {
    this.userId = localStorage.getItem('user.id');

    this.essayTypes = [];

    this.returnData = new QuickEssayReturnData;

    this.essay = new Essay();
    this.essay.initializeNewEssay();

    this.essayService.getApiEssayTypes().subscribe((response) => {
      for (const type of response.data) {
        const newType = new EssayType();
        newType.initializeNewEssayType();

        newType.mapEssayType(type);

        this.essayService.setEssayType(newType);
      }

      this.essayTypes = this.essayService.getEssayTypes();
    });

    this.essayService.getApiEssays(
      '/essay_users',
      this.userId,
      '0',
      null,
      null,
      false,
      null,
      false).subscribe(response => {

      this.searchEssays = response.essays;

      this.essayTitleFilteredOptions = this.essayTitleAutocompleteControl.valueChanges.pipe(
        startWith(''),
        map(essay => this._filterEssayTitle(essay))
      );
    });
  }

  ngOnInit(): void {
  }

  ngAfterViewInit() {
    this.activateCreateForm().then();
  }

  onNoClick(): void {
    this.dialogRef.close();
  }

  saveExistingEssay(essay) {
    this.returnData.essay = essay;
    this.returnData.isExisting = true;

    this.dialogRef.close(this.returnData);
  }

  saveNewEssay() {
    this.returnData.essay = this.essay;
    this.returnData.isExisting = false;

    this.dialogRef.close(this.returnData);
  }

  displayEssayName(essay: Essay) {
    if (essay) {
      this.essayTitleFieldDisplayValue = '';

      if (essay.title) {
        this.essayTitleFieldDisplayValue = essay.title;
      }
    }

    return this.essayTitleFieldDisplayValue;
  }

  private _filterEssayTitle(filterValue: any): Essay[] {
    if (filterValue && typeof filterValue === 'string') {
      filterValue = filterValue.toLowerCase();

      return this.searchEssays.filter(essay => {

        if (essay.title) {
          return essay.title.toLowerCase().includes(filterValue);
        } else {
          return null;
        }
      });
    }
  }

  selectEssayType(option, value) {
    if (value && option) {
      return option.id === value.id;
    } else {
      return null;
    }
  }

  async activateCreateForm() {
    await Sleep.wait(500);

    try {
      document.getElementById('existing_essay_label').focus();
    } catch(e) {
      document.getElementById('essay_label').focus();
    }
  }
}

import { Component, Inject } from '@angular/core';

import { MatBottomSheetRef, MAT_BOTTOM_SHEET_DATA } from '@angular/material/bottom-sheet';

import { Timeline } from '../../models/timelines/timeline';

import { TimelineService } from '../../services/timeline.service';
import {TimelineCategory} from '../../models/timelines/timeline-category';
import {Category} from '../../models/category';
import {EventNote} from '../../models/events/event-note';
import {TimelineEvent} from '../../models/timelines/timeline-event';

export interface DialogData {
  timelineEvent: TimelineEvent;
  timeline: Timeline;
  categoryEvents: Array<Category>;
}

@Component({
  selector: 'app-timeline-event-details',
  templateUrl: './timeline-event-details.component.html',
  styleUrls: ['./timeline-event-details.component.scss']
})

export class TimelineEventDetailsComponent {
  public isShadow: boolean;
  public selectedCategoryId: number;
  public selectedCategory: TimelineCategory;
  public categoryEventId: number;

  public isEditMode: boolean;

  public isInCategory: boolean;

  public displayNotes: EventNote[];

  public numberOfNotesToShow: number;
  public numberOfAdditionalNotes: number;

  public eventLink: string;

  constructor(public dialogRef: MatBottomSheetRef<TimelineEventDetailsComponent>,
              @Inject(MAT_BOTTOM_SHEET_DATA) public data: DialogData,
              public timelineService: TimelineService) {

    const self = this;

    this.isShadow = data.timelineEvent.isShadow;
    this.selectedCategoryId = 0;
    this.categoryEventId = 0;
    this.selectedCategory = null;
    this.isInCategory = false;
    this.isEditMode = false;

    this.displayNotes = [];
    this.numberOfNotesToShow = 2;
    this.numberOfAdditionalNotes = 0;

    // check if this event is in a category or not
    for (const category of this.data.timeline.categories) {
      if (!category.events) {
        category.events = [];
      }

      function idFound(ids) {
        return ids[1] === self.data.timelineEvent.id.toString();
      }

      const categoryEventIndex = category.events.findIndex(idFound);

      if (categoryEventIndex >= 0) {
        this.isInCategory = true;
        this.selectedCategory = category;
        this.selectedCategoryId = category.id;
        this.categoryEventId = category.events[categoryEventIndex][0];
      }
    }

    if (this.data.timelineEvent.event.notes.length) {
      for (let i = 0; i < this.data.timelineEvent.event.notes.length; i++) {
        if (i < this.numberOfNotesToShow) {
          this.displayNotes.push(this.data.timelineEvent.event.notes[i]);
          continue;
        }

        break;
      }
    }

    if (this.data.timelineEvent.event.notes.length > this.displayNotes.length) {
      this.numberOfAdditionalNotes = this.data.timelineEvent.event.notes.length - this.displayNotes.length;
    }

    this.eventLink = '/manager/events/' + this.data.timelineEvent.event.id.toString();
  }

  onNoClick(): void {
    this.dialogRef.dismiss();
  }

  updateShadowState() {
    // this is a change event, but does not have access to the event at the moment of the click event.
    // As such false is equal to true and the other way around
    let shadowState = false;

    if (!this.isShadow) {
      shadowState = true;
    }

    this.data.timelineEvent.isShadow = shadowState;

    this.timelineService.patchEventApiTimeline(this.data.timelineEvent, this.data.timeline).subscribe(() => { });
  }

  addToCategory() {
    let categoryToAdd: TimelineCategory = new TimelineCategory();

    for (const category of this.data.timeline.categories) {
      if (category.id === this.selectedCategoryId) {
        categoryToAdd = category;
      }
    }

    // event was not in a category and is being added to one
    if (!this.selectedCategory) {
      this.timelineService.createCategoryEventApiTimeline(categoryToAdd, this.data.timelineEvent.event).subscribe(response => {
        this.categoryEventId = response.data.id;
        this.selectedCategory = categoryToAdd;
        this.selectedCategoryId = categoryToAdd.id;
        this.isInCategory = true;

        this.addCategoryEventToTimeline(this.data.timelineEvent, this.categoryEventId);
      });

    } else {
      // event was in a category and is now being removed from all categories
      if (this.selectedCategoryId == 0) {
        this.timelineService.removeCategoryEventApiTimeline(this.categoryEventId).subscribe(() => {
          this.removeCategoryEventFromTimeline(this.data.timelineEvent, this.categoryEventId, false);

          this.selectedCategoryId = 0;
          this.categoryEventId = 0;
          this.selectedCategory = null;
          this.isInCategory = false;
        });

        // event is was in category A and is being updated to category B
      } else {
        this.timelineService.removeCategoryEventApiTimeline(this.categoryEventId).subscribe(() => {
          this.removeCategoryEventFromTimeline(this.data.timelineEvent, this.categoryEventId, true);

          this.timelineService.createCategoryEventApiTimeline(categoryToAdd, this.data.timelineEvent.event).subscribe(secondResponse => {
            this.categoryEventId = secondResponse.data.id;
            this.selectedCategory = categoryToAdd;
            this.selectedCategoryId = categoryToAdd.id;
            this.isInCategory = true;

            this.addCategoryEventToTimeline(this.data.timelineEvent, this.categoryEventId);
          });
        });
      }
    }
  }

  addCategoryEventToTimeline(event, categoryEventId) {
    // add it to the list of available categories
    this.selectedCategory.events.push([categoryEventId, event.id]);

    // if this is the first event in the category add the category to the data object first
    let thisCategory = null;

    for (const category of this.data.categoryEvents) {
      if (category.id === this.selectedCategoryId) {
        thisCategory = category;
      }
    }

    if (!thisCategory) {
      thisCategory = new Category();
      thisCategory.id = this.selectedCategory.id;
      thisCategory.label = this.selectedCategory.label;
      thisCategory.events = [];
      thisCategory.people = [];

      this.data.categoryEvents.push(thisCategory);
    }

    // add the event to the categoryEvents object so it reflects on the timeline
    for (const category of this.data.categoryEvents) {
      // remove event from the generic category if it's there
      if (!category.id) {
        if (event.formattedStartYear === event.formattedEndYear) {
          for (let i = 0; i < category.singlePointEvents.length; i++) {
            if (category.singlePointEvents[i].id === event.id) {
              category.singlePointEvents.splice(i, 1);
            }
          }
        } else {
          for (let i = 0; i < category.multiPointEvents.length; i++) {
            if (category.multiPointEvents[i].id === event.id) {
              category.multiPointEvents.splice(i, 1);
            }
          }
        }
      }

      // add the event to the appropriate category
      if (category.id === this.selectedCategoryId) {
        if (event.formattedStartYear === event.formattedEndYear) {
          category.singlePointEvents.push(event);
        } else {
          category.multiPointEvents.push(event);
        }
      }
    }
  }

  removeCategoryEventFromTimeline(event, categoryEventId, isUpdate) {
    // remove it from the list of available categories
    for (const category of this.data.timeline.categories) {
      if (category.events.length) {
        for (let i = 0; i < category.events.length; i++) {
          if (category.events[i][1] === event.id && category.events[i][0] === categoryEventId) {
            category.events.splice(i, 1);
          }
        }
      }
    }

    // remove the event from the category events so that it is reflected on the timeline
    for (const category of this.data.categoryEvents) {
      // if actually removing from all categories and not just updating it to a new category then add it back to the generic category
      if (!isUpdate) {
        if (!category.id) {
          if (event.formattedEndYear === event.formattedStartYear) {
            category.singlePointEvents.push(event);
          } else {
            category.multiPointEvents.push(event);
          }
        }
      }

      if (category.id === this.selectedCategory.id) {
        if (event.formattedEndYear === event.formattedStartYear) {
          for (let i = 0; i < category.singlePointEvents.length; i++) {
            if (category.singlePointEvents[i].id === event.id) {
              category.singlePointEvents.splice(i, 1);
            }
          }
        } else {
          for (let i = 0; i < category.multiPointEvents.length; i++) {
            if (category.multiPointEvents[i].id === event.id) {
              category.multiPointEvents.splice(i, 1);
            }
          }
        }

        // if there are no more events in this category remove it from the main categoryEvents object
        if (!category.singlePointEvents.length && !category.multiPointEvents.length) {
          for (let i = 0; i < this.data.categoryEvents.length; i++) {
            if (category.id === this.data.categoryEvents[i].id) {
              this.data.categoryEvents.splice(i, 1);
            }
          }
        }
      }
    }
  }
}

