import {ChartType} from '../enums/chart-types';
import {ChartLabel} from './chart-label';
import {ChartDataset} from './chart-dataset';
import {ChartOptions} from './chart-options';

export class Chart {
  id: number;

  title: string;
  type: ChartType;

  labels: ChartLabel[];
  datasets: ChartDataset[];

  options: ChartOptions;

  initializeNewChart() {
    this.title = 'New Chart';
    this.type = ChartType.LINE;

    this.labels = [];
    this.datasets = [];

    const initialLabel = new ChartLabel();
    initialLabel.initializeNewChartLabel();

    this.labels.push(initialLabel);
    this.labels.push(initialLabel);

    const initialDataset = new ChartDataset();
    initialDataset.initializeNewDataset();

    this.datasets.push(initialDataset);

    this.options = new ChartOptions();
    this.options.initializeNewOptions();
  }

  mapChart(chart: any) {
    this.id = chart.id;

    this.title = chart.attributes.title;

    if (chart.attributes.type === ChartType.LINE) {
      this.type = ChartType.LINE;
    } else if (chart.attributes.type === ChartType.BAR) {
      this.type = ChartType.BAR;
    } else if (chart.attributes.type === ChartType.HORIZONTAL_BAR) {
      this.type = ChartType.HORIZONTAL_BAR;
    } else if (chart.attributes.type === ChartType.PIE) {
      this.type = ChartType.PIE;
    } else if (chart.attributes.type === ChartType.DOUGHNUT) {
      this.type = ChartType.DOUGHNUT;
    } else if (chart.attributes.type === ChartType.POLAR_AREA) {
      this.type = ChartType.POLAR_AREA;
    } else if (chart.attributes.type === ChartType.RADAR) {
      this.type = ChartType.RADAR;
    }

    if (chart.attributes.chart_label && chart.attributes.chart_label.data.length) {
      this.labels = [];

      for (const chartLabel of chart.attributes.chart_label.data) {
        const newChartLabel = new ChartLabel();
        newChartLabel.initializeNewChartLabel();
        newChartLabel.mapChartLabel(chartLabel);

        newChartLabel.label = chartLabel.attributes.label;

        this.labels.push(newChartLabel);
      }
    }

    if (chart.attributes.chart_dataset && chart.attributes.chart_dataset.data.length) {
      this.datasets = [];

      for (const dataset of chart.attributes.chart_dataset.data) {
        const newDataset = new ChartDataset();
        newDataset.initializeNewDataset();
        newDataset.mapDataSet(dataset);

        this.datasets.push(newDataset);
      }
    }

    if (chart.attributes.chart_options && chart.attributes.chart_options.data.length) {
      const options = new ChartOptions();
      options.initializeNewOptions();

      options.mapOptions(chart.attributes.chart_options.data[0]);

      this.options = options;
    }
  }
}

import {ChartDatasetData} from './chart-dataset-data';

export class ChartDataset {
  id: number;
  label: string;
  data: ChartDatasetData[];
  backgroundColor: string;
  fill: boolean;
  borderColor: string;
  pointRadius: number;
  pointBackgroundColor: string;

  initializeNewDataset() {
    this.label = 'Auto Generated Dataset';
    this.data = [];
    this.backgroundColor = '#d56767';
    this.borderColor = '#6666ff';
    this.fill = false;
    this.pointRadius = 3;
    this.pointBackgroundColor = '#51ff86';

    const initialData = new ChartDatasetData();
    initialData.initializeNewDatasetData();

    this.data.push(initialData);
    this.data.push(initialData);
  }

  mapDataSet(dataset) {
    this.id = dataset.id;
    this.label = dataset.attributes.label;

    if (dataset.attributes.background_color) {
      this.backgroundColor = dataset.attributes.background_color;
    }

    if (dataset.attributes.border_color) {
      this.borderColor = dataset.attributes.border_color;
    }

    this.fill = dataset.attributes.fill;

    if (dataset.attributes.point_radius) {
      this.pointRadius = dataset.attributes.point_radius;
    }

    if (dataset.attributes.point_background_color) {
      this.pointBackgroundColor = dataset.attributes.point_background_color;
    }

    if (dataset.attributes.chart_dataset_data && dataset.attributes.chart_dataset_data.data.length) {
      this.data = [];

      for (const datasetData of dataset.attributes.chart_dataset_data.data) {
        const newDatasetData = new ChartDatasetData();
        newDatasetData.initializeNewDatasetData();
        newDatasetData.mapDatasetData(datasetData);

        this.data.push(newDatasetData);
      }
    }
  }
}

export class ChartDatasetData {
  id: number;

  xData: number;
  yData: number;
  rData: number;

  color: string;
  label: string;

  initializeNewDatasetData() {
    this.xData = 1;
    this.yData = 1;
    this.rData = 1;

    this.color = '#232bc4';
    this.label = 'Temp Label';
  }

  mapDatasetData(datasetData) {
    this.id = datasetData.id;

    if (datasetData.attributes.x_data) {
      this.xData = datasetData.attributes.x_data;
    }

    if (datasetData.attributes.y_data) {
      this.yData = datasetData.attributes.y_data;
    }

    if (datasetData.attributes.r_data) {
      this.rData = datasetData.attributes.r_data;
    }
  }
}

