    this.bodyFontStyle = 'normal';
    this.bodyFontColor = '#fff';
    this.bodyAlign = 'left';
    this.bodySpacing = 2;
    this.footerFontFamily = '\'Helvetica Neue\', \'Helvetica\', \'Arial\', \'sans-serif\'';
