    this.caretSize = 5;
    this.cornerRadius = 6;
    this.displayColors = true;
    this.borderColor = '#d9dedb';
    this.borderWidth = 0;
  }

  mapTooltipOptions(tooltipOptions) {
    this.id = tooltipOptions.id;

    this.enabled = tooltipOptions.attributes.enabled;

    if (tooltipOptions.attributes.background_color) {
