import { Component, OnInit, Input, Output, EventEmitter } from '@angular/core';

import { Author } from '../../../models/author';

@Component({
  selector: 'app-author-card',
  templateUrl: './author-card.component.html',
  styleUrls: ['./author-card.component.scss']
})
export class AuthorCardComponent implements OnInit {
  @Input() public author: Author;
  @Input() public canDelete: boolean;
  @Input() public isSourceAttachment: boolean;

  @Output() private removeAuthor: EventEmitter<Author>;

  constructor() {
    this.removeAuthor = new EventEmitter<Author>();

    if (!this.isSourceAttachment) {
      this.isSourceAttachment = false;
    }
  }

  ngOnInit() { }

  doRemoveAuthor() {
    this.removeAuthor.emit(this.author);
  }
}

import { Component, OnInit } from '@angular/core';
import {ActivatedRoute} from '@angular/router';
import {Author} from '../../../models/author';
import {AuthorService} from '../../../services/author.service';

@Component({
  selector: 'app-author-details',
  templateUrl: './author-details.component.html',
  styleUrls: ['./author-details.component.scss']
})
export class AuthorDetailsComponent implements OnInit {
  public author: Author;

  public isEditAuthorMode: boolean;

  constructor(private route: ActivatedRoute,
              private authorService: AuthorService) {

    const authorId = this.route.snapshot.paramMap.get('id');

    this.authorService.getApiAuthor(authorId).subscribe(author => {
      this.author = author;

      this.authorService.setAuthor(this.author);
    });

    this.isEditAuthorMode = false;
  }

  ngOnInit() { }

  activateEditAuthorMode() {
    this.isEditAuthorMode = true;
  }

  deactivateEditAuthorMode() {
    this.isEditAuthorMode = false;
  }

  editAuthor() {
    return this.authorService.patchApiAuthor(this.author).subscribe(() => {
      this.isEditAuthorMode = false;
    });
  }
}

import { Component, OnInit } from '@angular/core';

import { MatDialog } from '@angular/material/dialog';

import { Author } from '../../models/author';

import { AuthorService } from '../../services/author.service';
import {QuickAuthorComponent} from './quick-author/quick-author.component';
import {ConfirmRemovalComponent} from '../../utilities/confirm-removal/confirm-removal.component';

@Component({
  selector: 'app-authors',
  templateUrl: './authors.component.html',
  styleUrls: ['./authors.component.scss']
})

export class AuthorsComponent implements OnInit {
  public authors: Author[];
  public author: Author;

  public authorLink: string;

  public totalResults: number;
  public nextPage: string;
  public previousPage: string;

  constructor(private authorService: AuthorService, public dialog: MatDialog) {
    this.author = new Author();
    this.author.initializeAuthor();

    this.authorLink = '';

    this.getAuthors('/authors?sort=last_name&page%5Bnumber%5D=1');
  }

  ngOnInit() { }

  getAuthors(path) {
    this.authorService.getApiAuthors(path).subscribe(response => {
      for (const author of response.authors) {
        this.authorService.setAuthor(author);
      }

      this.authors = this.authorService.getAuthors();

      this.totalResults = response.total;
      this.nextPage = response.links.next;
      this.previousPage = response.links.prev;
    });
  }

  createAuthor() {
    const dialogRef = this.dialog.open(QuickAuthorComponent, {
      width: '750px'
    });

    dialogRef.afterClosed().subscribe(author => {
      if (author) {
        this.authorService.createApiAuthor(author).subscribe(response => {
          author.id = response.data.id;

          this.authors.unshift(author);
        });
      }
    });
  }

  removeAuthor(author) {
    const dialogRef = this.dialog.open(ConfirmRemovalComponent, {
      width: '250px',
      data: {
        label: 'the author ' + author.firstName + ' ' + author.lastName,
        content: ''
      }
    });

    dialogRef.afterClosed().subscribe(doClose => {
      if (doClose) {
        this.authorService.removeApiAuthor(author).subscribe(() => {
          this.authorService.removeAuthor(author);
        });
      }
    });
  }

  turnPage(author) {
    if (author.pageIndex < author.previousPageIndex) {
      this.getAuthors(this.previousPage);
    } else if (author.pageIndex > author.previousPageIndex) {
      this.getAuthors(this.nextPage);
    }
  }
}

