import { Component, OnInit } from '@angular/core';
import {ActivatedRoute, Router} from '@angular/router';

import { MatDialog } from '@angular/material/dialog';

import {Subject} from 'rxjs';

import { Chart } from '../../../models/chart';

import {ChartService} from '../../../services/chart.service';
import {ChartLabel} from '../../../models/chart-label';
import {ChartDatasetData} from '../../../models/chart-dataset-data';
import {ChartDataset} from '../../../models/chart-dataset';

import {ConfirmRemovalComponent} from '../../../utilities/confirm-removal/confirm-removal.component';

@Component({
  selector: 'app-chart',
  templateUrl: './chart.component.html',
  styleUrls: ['./chart.component.scss']
})
export class ChartComponent implements OnInit {
  public chart: Chart;

  public chartScreenSize: string;

  public updateChartNotifier: Subject<any> = new Subject<any>();

  /* ***
    OF NOTE: THERE IS THE ACTUAL ANGULAR CHART OBJECT USED TO RETRIEVE, UPDATE, CREATE AND DELETE CHARTS ON
    THE API SIDE. THEN THERE IS THE CHARTDATA OBJECT THAT REPRESENTS THE OBJECT FORM REQUIRED BY CHARTJS IN
    ORDER TO RENDER THE CHART ITSELF. ALL BINDING IS DONE WITH THE ANGULAR CHART OBJECT AND MAPPED TO THE
    CHARTJS OBJECT.
  *** */
  constructor(private route: ActivatedRoute,
              private navigation: Router,
              private chartService: ChartService,
              public dialog: MatDialog) {
    const chartId = this.route.snapshot.paramMap.get('id');

    this.chartService.getApiChart(chartId).subscribe(chart => {
      this.chart = chart;

      this.chart.labels.sort(this.sortById);

      for (const dataset of this.chart.datasets) {
        dataset.data.sort(this.sortById);
      }

      this.chartService.setChart(chart);
    });

    this.chartScreenSize = 'fullscreen_exit';
  }

  ngOnInit() { }

  private sortById(a, b) {
    if (a.id > b.id) {
      return 1;
    } else {
      return -1
    }
  }

  removeChart() {
    const dialogRef = this.dialog.open(ConfirmRemovalComponent, {
      width: '250px',
      data: {
        label: 'the chart '
      }
    });

    dialogRef.afterClosed().subscribe(doClose => {
      if (doClose) {
        this.chartService.deleteApiChart(this.chart.id).subscribe(() => {
          this.navigation.navigateByUrl('/manager/charts').then();
        });
      }
    });
  }

  updateChartType() {
    this.chartService.patchApiChart(this.chart).subscribe(() => {
      this.updateChartNotifier.next(true);
    });
  }

  addChartLabel() {
    const newChartLabel: ChartLabel = new ChartLabel();

    newChartLabel.initializeNewChartLabel();

    this.chartService.createApiChartLabel(this.chart, newChartLabel).subscribe((newLabelResponse) => {
      newChartLabel.id = newLabelResponse.data.id;

      this.chart.labels.push(newChartLabel);

      for (const dataset of this.chart.datasets) {
        const newDatasetData = new ChartDatasetData();
        newDatasetData.initializeNewDatasetData();
        newDatasetData.xData = dataset.data.length;

        dataset.data.push(newDatasetData);

        this.chartService.createApiChartDatasetData(dataset, newDatasetData).subscribe((datasetDataResponse => {
          newDatasetData.id = datasetDataResponse.id;
        }));
      }

      this.callUpdateChart();
    });
  }

  addDataset() {
    const dataset = new ChartDataset();
    dataset.initializeNewDataset();

    dataset.data = [];

    this.chartService.createApiChartDataset(this.chart, dataset).subscribe(newDatasetResponse => {
      dataset.id = newDatasetResponse.data.id;

      this.chart.labels.forEach(() => {
        const datasetData = new ChartDatasetData();
        datasetData.initializeNewDatasetData();

        this.chartService.createApiChartDatasetData(dataset, datasetData).subscribe((newDataResponse) => {
          datasetData.id = newDataResponse.data.id;
        });

        dataset.data.push(datasetData);
      });

      this.chart.datasets.push(dataset);

      this.callUpdateChart();
    });
  }

  updateChartLabel(label) {
    this.chartService.patchApiChartLabel(this.chart, label).subscribe(() => {
      this.callUpdateChart();
    });
  }

  updateDataset(dataset) {
    this.chartService.patchApiChartDataset(this.chart, dataset).subscribe(() => {
      this.callUpdateChart();
    });
  }

  updateDatasetData(dataset, data) {
    this.chartService.patchApiChartDatasetData(dataset, data).subscribe(() => {
      this.callUpdateChart();
    });
  }

  updateTitleOptions() {
    this.chartService.patchApiChartTitleOptions(this.chart.options, this.chart.options.title).subscribe(() => {
      this.callUpdateChart();
    });
  }

  updateTooltipOptions() {
    this.chartService.patchApiChartTooltipOptions(this.chart.options, this.chart.options.tooltips).subscribe(() => {
      this.callUpdateChart();
    });
  }

  updateLegendOptions() {
    this.chartService.patchApiChartLegendOptions(this.chart.options, this.chart.options.legend).subscribe(() => {
      this.callUpdateChart();
    });
  }

  updateLegendLabelOptions() {
    this.chartService.patchApiChartLegendLabelOptions(this.chart.options.legend, this.chart.options.legend.labels).subscribe(() => {
      this.callUpdateChart();
    });
  }

  callUpdateChart() {
    this.chartService.setChart(this.chart);

    this.updateChartNotifier.next();
  }

  toggleContentPanel(contentPanel) {
    if (contentPanel.opened) {
      this.chartScreenSize = 'fullscreen_exit';
      contentPanel.close();
    } else {
      this.chartScreenSize = 'fullscreen';
      contentPanel.open();
    }
  }

  trackByData(index) {
    return index;
  }
}

import {Component, Input, OnInit, OnDestroy, AfterViewInit} from '@angular/core';

import {Subject} from 'rxjs';

import { Chart as RenderedChart } from '../../../../../node_modules/chart.js/dist/Chart.js';

import {Chart} from '../../../models/chart';

import {ChartType} from '../../../enums/chart-types';

import {ChartService} from '../../../services/chart.service';

@Component({
  selector: 'app-chart-display',
  templateUrl: './chart-display.component.html',
  styleUrls: ['./chart-display.component.scss']
})
export class ChartDisplayComponent implements OnInit, OnDestroy, AfterViewInit {
  @Input() public notifier: Subject<any>;

  // in the case of a static display chart it can just be passed into this component.
  // however, if passing to an edit display than the chart will be retrieved from the service
  @Input() public chart: Chart;

  public chartConfig: any;
  public chartCanvasId: string;

  public renderedChart: any;

  private datasetColors = [
    '#39ab28',
    '#23c28f',
    '#3b99ca',
    '#2b66c4',
    '#554db7',
    '#715ab7',
    '#a53ab7',
    '#b73560',
    '#c40c09',
    '#d5531e',
    '#d48e01',
    '#6fab05',
    '#028014',
    '#009673',
    '#0461a9',
    '#354cb0',
    '#5747b0',
    '#6f2da6',
    '#b74783',
    '#da392f',
    '#ee6e1f',
    '#c39c3b',
  ];

  constructor(private chartService: ChartService) {
    this.chartConfig = {};
  }

  ngOnInit() {
    if (!this.chart) {
      this.chart = this.chartService.getChart();
    }

    this.chartCanvasId = 'chart_canvas_' + this.chart.id;

    // listens for request from the parent component to update that chart
    if (this.notifier) {
      this.notifier.subscribe(doDestroy => this.rerenderChart(doDestroy));
    }
  }

  ngOnDestroy() {
    if (this.notifier) {
      this.notifier.unsubscribe();
    }
  }

  ngAfterViewInit() {
    this.makeChart();
  }

  makeChartConfig() {
    this.chartConfig.type = this.chart.type;
    this.chartConfig.data = {};

    this.chartConfig.data.labels = this.chart.labels;

    this.chartConfig.data.datasets = [];

    if (!this.chartConfig.options) {
      this.chartConfig.options = {};
      this.chartConfig.options.title = {};
    }

    this.chartConfig.options.maintainAspectRatio = this.chart.options.maintainAspectRatio;
    this.chartConfig.options.responsive = this.chart.options.responsive;

    this.chartConfig.options.title = this.chart.options.title;
    this.chartConfig.options.legend = this.chart.options.legend;
    this.chartConfig.options.tooltips = this.chart.options.tooltips;

    this.chartConfig.options.scales = {

    };

    if (!this.chartConfig.data) {
      this.chartConfig.data = {};
      this.chartConfig.datasets = [];
    }

    for (const dataset of this.chart.datasets) {
      this.chartConfig.data.datasets.push(this.mapToChartJSDataset(dataset));
    }

    this.setChartCallbacks();
  }

  mapToChartJSDataset(dataset) {
    let chartJSDataset: any;

    chartJSDataset = {
      label: dataset.label,
      data: Object.keys(dataset.data).map(k => dataset.data[k].xData),
      fill: dataset.fill,
      backgroundColor: dataset.backgroundColor,
      borderColor: dataset.borderColor,
      pointRadius: dataset.pointRadius,
      pointBackgroundColor: dataset.pointBackgroundColor
    };

    if (this.chart.type === ChartType.PIE || this.chart.type === ChartType.DOUGHNUT) {
      chartJSDataset.backgroundColor = [];

      this.chart.labels.forEach(() => {
        chartJSDataset.backgroundColor.push(this.datasetColors.pop());
      });
    }

    return chartJSDataset;
  }

  makeChart() {
    const context = document.getElementById(this.chartCanvasId);

    this.makeChartConfig();

    this.renderedChart = new RenderedChart(context, this.chartConfig);
  }

  setChartCallbacks() {
    // The data object for values is of type object instead of String[]. The callback will return value into String[];
    if (this.chartConfig.type === ChartType.LINE || this.chartConfig.type === ChartType.BAR) {
      if (!this.chartConfig.options.scales) {
        this.chartConfig.options.scales = {};
      }

      if (!this.chartConfig.options.scales.xAxes) {
        this.chartConfig.options.scales.xAxes = [{
          ticks: {}
        }];
      }

      this.chartConfig.options.scales.xAxes[0].ticks.callback = (value, index, values) => value.label;

    } else if (this.chartConfig.type === ChartType.HORIZONTAL_BAR) {
      if (!this.chartConfig.options.scales) {
        this.chartConfig.options.scales = {};
      }

      if (!this.chartConfig.options.scales.yAxes) {
        this.chartConfig.options.scales.yAxes = [{
          ticks: {}
        }];
      }

      if (this.chartConfig.options.scales) {
        this.chartConfig.options.scales.yAxes[0].ticks.callback = (value, index, values) => value.label;
      }
    } else if (this.chartConfig.type === ChartType.PIE || this.chartConfig.type === ChartType.DOUGHNUT) {
      this.chartConfig.options.legend.labels.generateLabels = (chart) => {
        const data = chart.data;

        const theHelp = RenderedChart.helpers;

        if (data.labels.length && data.datasets.length) {
          return data.labels.map((label, i) => {
            const meta = chart.getDatasetMeta(0);
            const ds = data.datasets[0];
            const arc = meta.data[i];
            const custom = arc && arc.custom || {};
            const getValueAtIndexOrDefault = theHelp.getValueAtIndexOrDefault;
            const arcOpts = chart.options.elements.arc;
            const fill = custom.backgroundColor ? custom.backgroundColor :
              getValueAtIndexOrDefault(ds.backgroundColor, i, arcOpts.backgroundColor);
            const stroke = custom.borderColor ? custom.borderColor : getValueAtIndexOrDefault(ds.borderColor, i, arcOpts.borderColor);
            const bw = custom.borderWidth ? custom.borderWidth : getValueAtIndexOrDefault(ds.borderWidth, i, arcOpts.borderWidth);

            return {
              // And finally :
              text: label.label,
              fillStyle: fill,
              strokeStyle: stroke,
              lineWidth: bw,
              hidden: isNaN(ds.data[i]) || meta.data[i].hidden,
              index: i
            };
          });
        }
      };

      this.chartConfig.options.tooltips.callbacks = {};
      this.chartConfig.options.tooltips.callbacks.label = (tooltipItem, data) => {
        const dataValue = data.datasets[tooltipItem.datasetIndex].data[tooltipItem.index];
        const labelValue = data.labels[tooltipItem.index].label;

        return labelValue + ': ' + dataValue;
      };
    }
  }

  rerenderChart(doDestroy: boolean) {
    this.chart = this.chartService.getChart();

    if (doDestroy) {
      this.chartConfig = {};

      this.renderedChart.destroy();
      this.makeChartConfig();

      const context = document.getElementById(this.chartCanvasId);
      this.renderedChart = new RenderedChart(context, this.chartConfig);

    } else {
      this.makeChartConfig();

      this.renderedChart.update(this.chartConfig);
    }
  }
}

import { Component, OnInit } from '@angular/core';
import {Router} from '@angular/router';

import {ChartService} from '../../services/chart.service';
import {Chart} from '../../models/chart';
import {AddChartDialogComponent} from '../../utilities/add-chart-dialog/add-chart-dialog.component';
import {MatDialog} from '@angular/material/dialog';

@Component({
  selector: 'app-charts',
  templateUrl: './charts.component.html',
  styleUrls: ['./charts.component.scss']
})
export class ChartsComponent implements OnInit {
  public charts: Chart[];

  public totalResults: number;
  public nextPage: string;
  public previousPage: string;

  constructor(private chartService: ChartService,
              private router: Router,
              public dialog: MatDialog) {
    this.getCharts('/charts');
  }

  ngOnInit() {

  }

  getCharts(path) {
    this.chartService.getApiCharts(path).subscribe((response) => {
      for (const chart of response.charts) {
        this.chartService.setChart(chart);
      }

      this.charts = this.chartService.getCharts();

      this.totalResults = response.total;
      this.nextPage = response.links.next;
      this.previousPage = response.links.prev;
    });
  }

  createNewChart() {
    const dialogRef = this.dialog.open(AddChartDialogComponent, {
      width: '750px',
      data: {
        showExisting: false,
        showNew: true
      }
    });

    dialogRef.afterClosed().subscribe(chartResponse => {
      if (chartResponse.chart.title) {
        let chart = chartResponse.chart;

        this.chartService.createApiChart(chart).subscribe(response => {
          chart.id = response.data.id;

          for (const label of chart.labels) {
            this.chartService.createApiChartLabel(chart, label).subscribe(labelResponse => {
              label.id = labelResponse.data.id;
            });
          }

          for (const dataset of chart.datasets) {
            this.chartService.createApiChartDataset(chart, dataset).subscribe(datasetResponse => {
              dataset.id = datasetResponse.data.id;

              for (const data of dataset.data) {
                this.chartService.createApiChartDatasetData(dataset, data).subscribe(dataResponse => {
                  data.id = dataResponse.data.id;
                });
              }
            });
          }

          this.chartService.createApiChartOptions(chart, chart.options).subscribe(optionsResponse => {
            chart.options.id = optionsResponse.data.id;

            // make the title options
            this.chartService.createApiChartTitleOptions(chart.options, chart.options.title).subscribe(titleOptionsResponse => {
              chart.options.title.id = titleOptionsResponse.data.id;
            });

            // make the legend options
            this.chartService.createApiChartLegendOptions(chart.options, chart.options.legend).subscribe(legendOptionsResponse => {
              chart.options.legend.id = legendOptionsResponse.data.id;

              this.chartService.createApiChartLegendLabelOptions(chart.options.legend, chart.options.legend.labels)
                .subscribe(legendLabelOptionsResponse => {
                  chart.options.legend.labels.id = legendLabelOptionsResponse.data.id;
                });
            });

            // make the tooltip options
            this.chartService.createApiChartTooltipOptions(chart.options, chart.options.tooltips).subscribe(tooltipOptionsResponse => {
              chart.options.tooltips.id = tooltipOptionsResponse.data.id;
            });
          });

          this.router.navigate(['/manager/charts', response.data.id]).then();
        });
      }
    });
  }

  turnPage(chart) {
    if (chart.pageIndex < chart.previousPageIndex) {
      this.getCharts(this.previousPage);
    } else if (chart.pageIndex > chart.previousPageIndex) {
      this.getCharts(this.nextPage);
    }
  }
}

