import { Component, OnInit } from '@angular/core';
import {FormControl} from '@angular/forms';

import { MatDialogRef } from '@angular/material/dialog';

import {Observable} from 'rxjs';
import {map, startWith} from 'rxjs/operators';

import {PersonService} from '../../../services/person.service';

import {Person} from '../../../models/persons/person';

@Component({
  selector: 'app-editor-select-person',
  templateUrl: './editor-select-person.component.html',
  styleUrls: ['./editor-select-person.component.scss']
})
export class EditorSelectPersonComponent implements OnInit {
  public persons: Person[];

  public responseObject: any;

  public loadAutoComplete: boolean;

  public personsAutocompleteControl = new FormControl();
  public personsFilteredOptions: Observable<Person[]>;
  public personFieldDisplayValue: string;

  constructor(public dialogRef: MatDialogRef<EditorSelectPersonComponent>, private personService: PersonService) {
    this.responseObject = {
      person: null
    };

    this.loadAutoComplete = false;

    this.personService.getApiPersons('/persons?page[size]=0&fields[person]=first_name,middle_name,last_name', null, null, false)
      .subscribe(persons => {

      for (const person of persons.persons) {
        this.personService.setPerson(person);
      }

      this.persons = this.personService.getPersons();

      this.personsFilteredOptions = this.personsAutocompleteControl.valueChanges.pipe(
        startWith(''),
        map(event => this._filterPersons(event))
      );

      this.loadAutoComplete = true;
    });
  }

  ngOnInit() {
  }

  onNoClick(): void {
    this.dialogRef.close();
  }

  savePerson() {
    this.responseObject.person = this.personsAutocompleteControl.value;
  }

  displayPerson(person: Person) {
    if (person) {
      this.personFieldDisplayValue = person.firstName;

      if (person.middleName) {
        this.personFieldDisplayValue += ' ' + person.middleName;
      }

      if (person.lastName) {
        this.personFieldDisplayValue += ' ' + person.lastName;
      }
    }

    return this.personFieldDisplayValue;
  }

  private _filterPersons(filterValue: any): Person[] {
    if (filterValue && typeof filterValue === 'string') {
      filterValue = filterValue.toLowerCase();

      return this.persons.filter(person => {
        return person.firstName.toLowerCase().includes(filterValue) || person.lastName.toLowerCase().includes(filterValue);
      });
    }
  }
}

import { Component, OnInit } from '@angular/core';
import {FormControl} from '@angular/forms';

import {Observable} from 'rxjs';
import {map, startWith} from 'rxjs/operators';

import {Source} from '../../../models/source';

import {SourceService} from '../../../services/source.service';
import { MatDialogRef } from '@angular/material/dialog';

@Component({
  selector: 'app-editor-select-source',
  templateUrl: './editor-select-source.component.html',
  styleUrls: ['./editor-select-source.component.scss']
})
export class EditorSelectSourceComponent implements OnInit {
  public sources: Source[];

  public responseObject: any;

  public loadAutoComplete: boolean;

  public sourcesAutocompleteControl = new FormControl();
  public sourcesFilteredOptions: Observable<Source[]>;
  public sourceFieldDisplayValue: string;

  constructor(public dialogRef: MatDialogRef<EditorSelectSourceComponent>,
              private sourceService: SourceService) {
    this.responseObject = {
      source: null,
      chapter: '',
      startPage: 0,
      endPage: 0
    };

    this.responseObject.chapter = localStorage.getItem('last_chapter') || '';

    this.loadAutoComplete = false;

    this.sourceService.getApiSources('/references?page[size]=0&fields[reference]=title,sub_title&sort=title').subscribe(sources => {
      for (const source of sources.sources) {
        this.sourceService.setSource(source);
      }

      this.sources = this.sourceService.getSources();

      this.sourcesFilteredOptions = this.sourcesAutocompleteControl.valueChanges.pipe(
        startWith(''),
        map(source => this._filterSources(source))
      );

      this.loadAutoComplete = true;
    });
  }

  ngOnInit() {
  }

  onNoClick(): void {
    this.dialogRef.close();
  }

  saveSource() {
    this.responseObject.source = this.sourcesAutocompleteControl.value;
  }

  displaySource(source: Source) {
    if (source) {
      this.sourceFieldDisplayValue = source.title;

      if (source.subTitle) {
        this.sourceFieldDisplayValue = this.sourceFieldDisplayValue + ': ' + source.subTitle;
      }
    }

    return this.sourceFieldDisplayValue;
  }

  private _filterSources(filterValue: any): Source[] {
    if (filterValue && typeof filterValue === 'string') {
      filterValue = filterValue.toLowerCase();

      return this.sources.filter(source => {
        return source.title.toLowerCase().includes(filterValue);
      });
    }
  }
}

import { Component, OnInit } from '@angular/core';
import {FormControl} from '@angular/forms';

import { MatDialogRef } from '@angular/material/dialog';

import {Observable} from 'rxjs';
import {map, startWith} from 'rxjs/operators';

import {Timeline} from '../../../models/timelines/timeline';

import {TimelineService} from '../../../services/timeline.service';

@Component({
  selector: 'app-editor-select-timeline',
  templateUrl: './editor-select-timeline.component.html',
  styleUrls: ['./editor-select-timeline.component.scss']
})
export class EditorSelectTimelineComponent implements OnInit {
  public timelines: Timeline[];

  public responseObject: any;

  public loadAutoComplete: boolean;

  public timelinesAutocompleteControl = new FormControl();
  public timelinesFilteredOptions: Observable<Timeline[]>;
  public timelineFieldDisplayValue: string;

  constructor(public dialogRef: MatDialogRef<EditorSelectTimelineComponent>, private timelineService: TimelineService) {
    this.responseObject = {
      timeline: null
    };

    this.loadAutoComplete = false;

    this.timelineService.getApiTimelines('/timelines', null, '0', null, ['label'], null, null, null, false).subscribe(timelines => {
      for (const timeline of timelines.timelines) {
        this.timelineService.setTimeline(timeline);
      }

      this.timelines = this.timelineService.getTimelines();

      this.timelinesFilteredOptions = this.timelinesAutocompleteControl.valueChanges.pipe(
        startWith(''),
        map(timeline => this._filterTimelines(timeline))
      );

      this.loadAutoComplete = true;
    });
  }

  ngOnInit() {
  }

  onNoClick(): void {
    this.dialogRef.close();
  }

  saveTimeline() {
    this.responseObject.timeline = this.timelinesAutocompleteControl.value;
  }

  displayTimeline(timeline: Timeline) {
    if (timeline) {
      this.timelineFieldDisplayValue = timeline.label;
    }

    return this.timelineFieldDisplayValue;
  }

  private _filterTimelines(filterValue: any): Timeline[] {
    if (filterValue && typeof filterValue === 'string') {
      filterValue = filterValue.toLowerCase();

      return this.timelines.filter(timeline => {
        return timeline.label.toLowerCase().includes(filterValue);
      });
    }
  }
}

