import { Component, OnInit } from '@angular/core';
import {ProjectService} from '../services/project.service';
import {Project} from '../models/projects/project';
import {AddProjectDialogComponent} from '../utilities/add-project-dialog/add-project-dialog.component';
import {MatDialog} from '@angular/material/dialog';
import {MessageDialogComponent} from '../utilities/message-dialog/message-dialog.component';
import {ConfirmRemovalComponent} from '../utilities/confirm-removal/confirm-removal.component';

@Component({
  selector: 'app-projects',
  templateUrl: './projects.component.html',
  styleUrls: ['./projects.component.scss']
})
export class ProjectsComponent implements OnInit {
  public projects: Project[];

  public userId: string;

  public totalResults: number;
  public nextPage: string;
  public previousPage: string;

  constructor(private projectService: ProjectService, public dialog: MatDialog) {
    this.userId = localStorage.getItem('user.id');

    this.getProjects(null, this.userId);
  }

  ngOnInit() {
  }

  getProjects(path, userId) {
    this.projectService.getApiProjects(path, userId).subscribe((response) => {
      for (const project of response.projects) {
        this.projectService.setProject(project);
      }

      this.projects = this.projectService.getProjects();
    });
  }

  addProject() {
    const dialogRef = this.dialog.open(AddProjectDialogComponent, {
      width: '750px'
    });

    dialogRef.afterClosed().subscribe(project => {
      this.projectService.createApiProject(project).subscribe((response) => {
        project.id = response.data.id;

        this.projectService.addUserToProject(project, this.userId).subscribe(() => { });

        this.projects.unshift(project);
      });
    });
  }

  deleteProject(project: Project) {
    const dialogRef = this.dialog.open(ConfirmRemovalComponent, {
      width: '250px',
      data: {
        label: 'the project '
      }
    });

    dialogRef.afterClosed().subscribe(doClose => {
      if (doClose) {
        this.projectService.deleteApiProject(project).subscribe(() => {
          for (let i = 0; i < this.projects.length; i++) {
            if (this.projects[i].id === project.id) {
              this.projects.splice(i, 1);
            }
          }
        });
      }
    });
  }

  turnPage(project) {
    if (project.pageIndex < project.previousPageIndex) {
      this.getProjects(this.previousPage, this.userId);
    } else if (project.pageIndex > project.previousPageIndex) {
      this.getProjects(this.nextPage, this.userId);
    }
  }
}

import { Component, OnInit, AfterViewInit } from '@angular/core';
import {AuthorService} from '../../../services/author.service';
import {Author} from '../../../models/author';
import {Observable} from 'rxjs';
import {FormControl} from '@angular/forms';
import { MatDialogRef } from '@angular/material/dialog';
import {map, startWith} from 'rxjs/operators';

import {Sleep} from '../../../utilities/sleep';

@Component({
  selector: 'app-quick-author',
  templateUrl: './quick-author.component.html',
  styleUrls: ['./quick-author.component.scss']
})
export class QuickAuthorComponent implements OnInit, AfterViewInit {
  public searchAuthors: Author[] = [];

  public authorFirstNameAutocompleteControl = new FormControl();
  public authorFirstNameFilteredOptions: Observable<Author[]>;
  public authorFirstNameFieldDisplayValue: string;

  public authorLastNameAutocompleteControl = new FormControl();
  public authorLastNameFilteredOptions: Observable<Author[]>;
  public authorLastNameFieldDisplayValue: string;

  public author: Author;

  constructor(private authorService: AuthorService,
              public dialogRef: MatDialogRef<QuickAuthorComponent>) {

    this.author = new Author();
    this.author.initializeAuthor();

    this.authorService.getApiAuthors('/authors?page[size]=0&fields[author]=first_name,last_name&sort=last_name')
      .subscribe(response => {

      this.searchAuthors = response.authors;

      this.authorFirstNameFilteredOptions = this.authorFirstNameAutocompleteControl.valueChanges.pipe(
        startWith(''),
        map(author => this._filterAuthorsFirstName(author))
      );

